SELECT a.*, b.name
FROM
(
SELECT id, userid, date, '羽毛球场' type, start_time, end_time, ispay, site_no, iscomplete
FROM tb_badminton


        date = #{date}


        AND start_time = #{start_time}


        AND end_time = #{end_time}


) a,
(
SELECT id, name
FROM tb_user


        name = #{name}


        phone = #{phone}


) b
WHERE a.userid = b.id
ORDER BY a.date DESC LIMIT #{start}, #{page_size}

SELECT id, userid, date, '羽毛球场' type, start_time, end_time, ispay, site_no, iscomplete, alias, phone
FROM tb_badminton
WHERE alias IS  NOT NULL AND alias != ''

    AND alias = #{alias}


    AND phone = #{phone}


    date = #{date}


    AND start_time = #{start_time}


    AND end_time = #{end_time}

SELECT a.*, b.name
FROM
(
    SELECT id, userid, date, '羽毛球场' type, start_time, end_time, ispay, site_no, alias
    FROM tb_badminton
    WHERE iscomplete = '1'

        AND date = #{date}


        AND start_time = #{start_time}


        AND end_time = #{end_time}

) a
LEFT JOIN
(
    SELECT id, name
    FROM tb_user


            name = #{name}


            phone = #{phone}


) b
ON a.userid = b.id
ORDER BY a.date DESC LIMIT #{start}, #{page_size}

SELECT a.*, b.name
FROM
(
    SELECT id, userid, date, '羽毛球场' type, start_time, end_time, ispay, site_no, alias
    FROM tb_badminton
    WHERE ispay = '1' AND iscomplete = '0'

        AND date = #{date}


        AND start_time = #{start_time}


        AND end_time = #{end_time}

) a
LEFT JOIN
(
    SELECT id, name
    FROM tb_user


            name = #{name}


            phone = #{phone}


) b
ON a.userid = b.id
ORDER BY a.date DESC LIMIT #{start}, #{page_size}

SELECT a.*, b.name
FROM
(
    SELECT id, userid, date, '羽毛球场' type, start_time, end_time, ispay, site_no, alias
    FROM tb_badminton
    WHERE ispay = '0'

        AND date = #{date}


        AND start_time = #{start_time}


        AND end_time = #{end_time}

) a
LEFT JOIN
(
    SELECT id, name
    FROM tb_user


            name = #{name}


            phone = #{phone}


) b
ON a.userid = b.id
ORDER BY a.date DESC LIMIT #{start}, #{page_size}

SELECT id, name, phone, site_no, '羽毛球场' type, start_time, end_time, week
FROM tb_badminton_long


        name = #{name}


        AND phone = #{phone}


        AND week = #{week}


        AND start_time = #{start_time}


        AND end_time = #{end_time}


ORDER BY week ASC
LIMIT #{start}, #{page_size}

SELECT id, '羽毛球场' type, name, phone, emergencycall, week, start_time, end_time, address, money, remark, site_no
FROM tb_badminton_long
WHERE id = #{id}
