WHERE a.userid = b.id

SELECT id, userid, date, '羽毛球场' type, start_time, end_time, ispay, site_no, iscomplete, alias, phone
FROM tb_badminton
WHERE alias IS  NOT NULL AND alias != ''

    AND alias = #{alias}


    AND phone = #{phone}


    date = #{date}


    AND start_time = #{start_time}


    AND end_time = #{end_time}

LIMIT #{start}, #{page_size}

