SELECT id, name, phone, site_no, '羽毛球场' type, start_time, end_time, week
FROM tb_badminton_long


        name = #{name}


        AND phone = #{phone}


        AND week = #{week}


        AND start_time = #{start_time}


        AND end_time = #{end_time}


ORDER BY week ASC
LIMIT #{start}, #{page_size}

SELECT count(*)
FROM tb_badminton_long


        name = #{name}


        AND phone = #{phone}


        AND week = #{week}


        AND start_time = #{start_time}


        AND end_time = #{end_time}

SELECT id, '羽毛球场' type, name, phone, emergencycall, week, start_time, end_time, address, money, remark, site_no
FROM tb_badminton_long
WHERE id = #{id}

mapper.update:
UPDATE tb_badminton
SET iscomplete = '1'
WHERE id = #{id}

UPDATE tb_badminton
SET ispay = '1'
WHERE id = #{id}

UPDATE tb_badminton_long
SET name = #{name}, phone = #{phone}, emergencycall = #{emergencycall}, week = #{week}, start_time = #{start_time},
end_time = #{end_time}, site_no = #{site_no}, address = #{address}, remark = #{remark}, money = #{money}
WHERE id = #{id}
