mapper.select:
SELECT a.*, b.name
FROM
(
  SELECT id, userid, "date", '篮球场' type, start_time, end_time, ispay, site_no
  FROM tb_basketball
  WHERE iscomplete = '1'

      AND "date" = #{date}


      AND start_time = #{start_time}


      AND end_time = #{end_time}

) a,
(
  SELECT id, name
  FROM tb_user


          name = #{name}


          phone = #{phone}


) b
WHERE a.userid = b.id
ORDER BY a."date" DESC LIMIT #{start}, #{page_size}

SELECT a.*, b.name
FROM
(
    SELECT id, userid, "date", '篮球场' type, start_time, end_time, ispay, site_no
    FROM tb_basketball
    WHERE ispay = '1' AND iscomplete = '0'

        AND "date" = #{date}


        AND start_time = #{start_time}


        AND end_time = #{end_time}

) a,
(
    SELECT id, name
    FROM tb_user


            name = #{name}


            phone = #{phone}


) b
WHERE a.userid = b.id
ORDER BY a."date" DESC LIMIT #{start}, #{page_size}

SELECT a.*, b.name
FROM
(
    SELECT id, userid, "date", '篮球场' type, start_time, end_time, ispay, site_no
    FROM tb_basketball
    WHERE ispay = '0'

        AND "date" = #{date}


        AND start_time = #{start_time}


        AND end_time = #{end_time}

) a,
(
SELECT id, name
    FROM tb_user


            name = #{name}


            phone = #{phone}


) b
WHERE a.userid = b.id
ORDER BY a."date" DESC LIMIT #{start}, #{page_size}
