SELECT a.*, b.name
FROM
(
    SELECT id, userid, date, '篮球场' type, start_time, end_time, ispay, site_no, alias
    FROM tb_basketball


            AND date = #{date}


            AND start_time = #{start_time}


            AND end_time = #{end_time}


) a,
(
    SELECT id, name
    FROM tb_user


            name = #{name}


            AND phone = #{phone}


) b
WHERE a.userid = b.id
ORDER BY a.date DESC LIMIT #{start}, #{page_size}

SELECT id, userid, date, '篮球场' type, start_time, end_time, ispay, site_no, iscomplete, alias, phone
FROM tb_basketball
WHERE alias IS  NOT NULL AND alias != ''

    AND alias = #{alias}


    AND phone = #{phone}


    AND date = #{date}


    AND start_time = #{start_time}


    AND end_time = #{end_time}

SELECT a.*, b.name
FROM
(
SELECT id, userid, date, '篮球场' type, start_time, end_time, ispay, site_no, iscomplete
FROM tb_basketball
WHERE iscomplete = '1'

    AND date = #{date}


    AND start_time = #{start_time}


    AND end_time = #{end_time}

) a,
(
SELECT id, name
FROM tb_user


        name = #{name}


        AND phone = #{phone}


) b
WHERE a.userid = b.id
ORDER BY a.date DESC LIMIT #{start}, #{page_size}

SELECT id, userid, date, '篮球场' type, start_time, end_time, ispay, site_no, iscomplete, alias, phone
FROM tb_basketball
