    AND date = #{date}


    AND start_time = #{start_time}


    AND end_time = #{end_time}

) a,
(
SELECT id, name
FROM tb_user


        name = #{name}


        AND phone = #{phone}


) b
WHERE a.userid = b.id
ORDER BY a.date DESC LIMIT #{start}, #{page_size}

