mapper.select:
SELECT a.*, b.name
FROM
(
SELECT id, userid, date, '乒乓球台' type, start_time, end_time, ispay, 0 site_no, iscomplete
FROM tb_pingpang


        date = #{date}


        AND start_time = #{start_time}


        AND end_time = #{end_time}


) a,
(
SELECT id, name
FROM tb_user


        name = #{name}


        phone = #{phone}


) b
WHERE a.userid = b.id
ORDER BY a.date DESC LIMIT #{start}, #{page_size}

SELECT COUNT(a.id) FROM
(
SELECT id, userid, date, '乒乓球台' type, start_time, end_time, ispay, 0 site_no, iscomplete
FROM tb_pingpang


        date = #{date}


        AND start_time = #{start_time}


        AND end_time = #{end_time}


) a,
(
SELECT id, name
FROM tb_user


        name = #{name}


        phone = #{phone}


) b
WHERE a.userid = b.id

SELECT id, userid, date, '乒乓球台' type, start_time, end_time, ispay, 0 site_no, iscomplete, alias, phone
FROM tb_pingpang
WHERE alias IS  NOT NULL AND alias != ''

    AND alias = #{alias}


    AND phone = #{phone}


    date = #{date}


    AND start_time = #{start_time}


    AND end_time = #{end_time}

LIMIT #{start}, #{page_size}

